#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll count=0;
        unordered_map<ll,ll> prev;
        for(int i=0;i<n;i++){
            unordered_map<ll,ll> curr;
            if(nums[i]==k){
                ++count;
            }
            curr[nums[i]]=1;
            for(auto& [v,f]:prev){
                ll nres=v&nums[i];
                if(nres==k){
                    count+=f;
                }
                curr[nres]+=f;
            }
            prev=curr;
        }
        return count;
    }
};
