int b[100005];
class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        memset(b, 0, k + 1 << 2);
        for (int i = 0; i * 2 < a.size(); ++i) {
            int x = a[i], y = a[a.size() - i - 1];
            if (x > y) {
                swap(x, y);
            }
            ++b[0];
            --b[y - x];
            ++b[y - x + 1];
            ++b[max(k - x, y) + 1];
        }
        for (int i = 1; i <= k; ++i) {
            b[i] += b[i - 1];
        }
        int res = a.size() / 2;
        for (int i = 0; i <= k; ++i) {
            res = min(res, b[i]);
        }
        return res;
    }
};
