class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        vector<int> can(k + 5);
        vector<int> have(k + 1);
        int n = (int)a.size();
        for(int i = 0; i < n / 2; i++){
            int mn = abs(a[i] - a[n - i - 1]);
            int mx = mn;
            have[mn]++;
            mn = 0;
            mn = min(mn,abs(k - a[n - i - 1]));
            mx = max(mx,abs(k - a[n - i - 1]));
            mn = min(mn,abs(k - a[i]));
            mx = max(mx,abs(k - a[i]));
            mn = min(mn,abs(a[n - i - 1]));
            mx = max(mx,abs(a[n - i - 1]));
            mn = min(mn,abs(a[i]));
            mx = max(mx,abs(a[i]));
            can[mn]++;
            can[mx + 1]--;
        }
        int pref = 0;
        int ans = 1e9;
        for(int i = 0; i <= k; i++){
            pref += can[i];
            int one = pref - have[i];
            int two = (n / 2 - pref);
            ans = min(ans,one + 2 * two);
        }
        return ans;
    }
};
