class Solution
{
public:
    int minChanges(vector<int> &nums, int k)
    {
        const int n = nums.size();
        vector<int> f(k + 1), d(k + 2);
        for (int i = 0; i < n / 2; i++)
        {
            int diff = abs(nums[i] - nums[n - i - 1]);
            f[diff]++;
            d[max({nums[i], k - nums[i], nums[n - i - 1], k - nums[n - i - 1]})]++;
        }
        int tot = 0, ans = n;
        for (int i = k; i >= 0; i--)
        {
            tot += d[i];
            ans=min(ans,tot - f[i] + (2 * ((n/2) - tot)));
            cout<<d[i]<<' ';
        }
        return ans;
    }
};

