class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>temp(k+5,0);
        int xd=0;
        for(int i=0;i<n/2;i++)
        {
            int x=nums[i];
            int y=nums[n-i-1];
            if(x>y)swap(x,y);
            int mini=1e9;
            int maxi=-1e9;
            mini=min(mini,abs(y-x));
            mini=min(mini,abs(y-k));
            mini=min(mini,abs(x-k));
            mini=min(mini,abs(y));
            mini=min(mini,abs(x));
            maxi=max(maxi,abs(y-x));
            maxi=max(maxi,abs(y-k));
            maxi=max(maxi,abs(x-k));
            maxi=max(maxi,abs(y));
            maxi=max(maxi,abs(x));
            int td=abs(x-y);
            int md=max(max(x,y),k-min(x,y));
            temp[0]++;
            temp[td]--;
            temp[td+1]++;
            temp[md+1]++;
        }
       int ans=1e9;
       for(int i=1;i<temp.size();i++)
       {
        temp[i]+=temp[i-1];
        ans=min(ans,temp[i]);
       }
       ans=min(ans,temp[0]);
       return ans;
    }
};
