class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> m1;
        int n=nums.size();
        for(int i=0;i<(n/2);i++){
            m1[abs(nums[i]-nums[n-i-1])]++;
        }
        int ans=INT_MAX;
        for(auto x:m1){
            if(((n - (x.second*2))/2)<ans){
            int z=0;
            for(int i=0;i<(n/2);i++){
                if(abs(nums[i]-nums[n-i-1])!=x.first){
                    if((min(nums[i],nums[n-i-1]) + x.first)>k && max(nums[i],nums[n-i-1])-x.first <0 ){
                        z+=2;
                    }else{
                        z++;
                    }
                }
            }
            ans=min(z,ans);
            }
        }
        return ans;
    }
};
