#include <vector>
#include <map>
#include <algorithm>
#include <cmath>

using namespace std;

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> diffMap;
        vector<int> values;

        int n = nums.size();
        for (int i = 0; i < n / 2; ++i) {
            int first = nums[i], second = nums[n - i - 1];
            int difference = abs(first - second);
            int maxVal = max({first, second, k - first, k - second});

            diffMap[difference].push_back(maxVal);
            values.push_back(maxVal);
        }

        int minChanges = n;
        sort(values.begin(), values.end());

        for (auto& entry : diffMap) {
            int difference = entry.first;
            int transformations = lower_bound(values.begin(), values.end(), difference) - values.begin();
            transformations = transformations * 2 + (n / 2 - transformations);

            for (auto& maxVal : entry.second) {
                if (maxVal < difference) {
                    transformations -= 2;
                } else {
                    transformations -= 1;
                }
            }

            minChanges = min(minChanges, transformations);
        }

        return minChanges;
    }
};
