class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp;
        vector<int> dp(k+2,0);
        for(int i=0;i<nums.size()/2;i++)
        {
            int x=abs(nums[i]-nums[nums.size()-1-i]);
            mp[x]++;
            int mx=max(nums[i],nums[nums.size()-1-i]);
            mx=max(mx,max(k-nums[i],k-nums[nums.size()-1-i]));
            dp[0]+=1;
            dp[mx+1]+=1;
        }
        for(int i=1;i<=k;i++)
            dp[i]+=dp[i-1];
        int mn=INT_MAX;
        for(int i=0;i<=k;i++)
        {
            int cnt=dp[i];
            if(mp.find(i) != mp.end()) cnt-=mp[i];
            mn=min(mn,cnt);
        }
        return mn;
    }
};
