class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mpp;
        vector<int> v;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            mpp[diff].push_back(X);
            v.push_back(X);
        }
        int ans = nums.size();
        sort(v.begin(),v.end());

        for(auto &it :mpp){

            int diff = it.first;
            int total = lower_bound(v.begin(),v.end(),diff)-v.begin();
            total = total*2+(nums.size()/2-total);

            for(auto &X:it.second){
                if(X<diff){
                    total-=2;
                }
                else{
                    total-=1;
                }
            }

            ans = min(ans,total);

        }

        return ans;
    }
};
