class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,vector<int>>mp;
        vector<int>temp;

        int n=nums.size();
        int finalans=n;
        for(int i=0;i<n/2;i++){
            int temp1=nums[i],temp2=nums[n-i-1];
            int difference=abs(temp1-temp2);
            int need=max({temp1,temp2,k-temp1,k-temp2});
            mp[difference].push_back(need);
            temp.push_back(need);
}
        sort(temp.begin(),temp.end());
        for(auto &val:mp){
            int diff=val.first;
            int T=lower_bound(temp.begin(),temp.end(),diff)-temp.begin();
            T=T*2 +(n/2-T);
            for(auto &X:val.second){
                if(X<diff) T-=2;
                else T-=1;
            }
            finalans=min(finalans,T);
        }
        return finalans;
    }
};
