#include <bits/stdc++.h>
using namespace std;
#define endl "\n"
#define ka long long int
#define ab(a,b) abs(a-b)
#define fr(n) for(int i=0;i<n;i++)
#define ki vector<int>
#define sor(vec) sort(vec.begin(), vec.end())
#define lb(vec,it) lower_bound(vec.begin(), vec.end(), it.first)
class Solution {
public:
    int minChanges(ki& arr, int k) {
        int res = arr.size(); 
        ki vec;
        map<int, ki> mp;  
        fr(arr.size()/2){
            int a = arr[arr.size() - i - 1], sub = ab(arr[i],a);
            int fl = max(arr[i], max(a, max(k - arr[i], k - a)));
            vec.push_back(fl);
            mp[sub].push_back(fl);
        }
        sor(vec); 
        for (auto& it : mp) {
            int flag = lb(vec,it) - vec.begin();
            flag =(arr.size() / 2 - flag)+ flag * 2 + 0;
            for (auto& fl : it.second) {
                if (fl>=it.first) flag -= 1; 
                else flag -= 2;
            }
            res = min(flag, res);
        }
        return res;
    }
};
