#include <bits/stdc++.h>
using namespace std;

#define ll long long
#define fr(i, a, b) for (int i = a; i < b; i++)
const int mod = 1e9 + 7;

class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n= a.size() ; 
        vector<int> d(k+2) ;

        vector<int> mp(k+2) ;
        int now = 0; 
        for(int  i = 0 ;i<n/2;i++)
        {
            int b = abs(a[i] - a[n-i-1]) ;
            int t = max(a[i],k-a[i])+1 ;
            t = max(t,max(a[n-i-1],k-a[n-i-1])+1) ;
            d[t]++ ;
            // cout<<b<<endl;
            mp[b]--;
            now++;
        }
        for(int i =0 ; i<=k;i++)
        {
            // cout<<now<<" "<<d[i]<<" "<<mp[i]<<"\n" ;
            // if(i==4) cout<<now<<endl;
            now += d[i] ;
            mp[i] += now ;
        }
        int ans = INT_MAX ;
        for(int i = 0 ; i<=k;i++)
        {
            // cout<<i<<" "<<mp[i]<<"\n" ;
            ans = min(ans , mp[i]) ; 
        }
        return ans ;

    }
};

