class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ans = 1e9, n = nums.size(), mmax = 0;
        map<int, int> mp;
        for(int i = 0; i < n/2; i++){
            mp[abs(nums[i] - nums[n - i - 1])]++;
        }
        for(auto a : mp){
            if(ans <= n/2 - a.second) continue;
            int as = 0;
            for(int i = 0; i < n/2; i++){
                if(abs(nums[i] - nums[n - i - 1]) != a.first){
                    if(k - min(nums[i], nums[n - i - 1]) >= a.first || max(nums[i], nums[n - i - 1]) >= a.first) as++;
                    else as += 2;
                }
            }   
            ans = min(as, ans);
        }
        return min(n/2, ans);
    }
};
