class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mpp;
        vector<int> v;
        for(int i=0;i<nums.size()/2;i++){
            int d=abs(nums[i]-nums[nums.size()-i-1]);
            int a=max({nums[i],nums[nums.size()-i-1],k-nums[i],k-nums[nums.size()-i-1]});
            mpp[d].push_back(a);
            v.push_back(a);
        }
        int res=nums.size();
        sort(v.begin(),v.end());
        for(const auto& [d,values]: mpp){
            int t=lower_bound(v.begin(),v.end(),d)-v.begin();
            t=t*2+(nums.size()/2-t);

            for(const auto& a:values){
                if(a<d){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }
            res=min(res,t);
        }
        return res;
    }
};
