class Solution {
public:
    int minChanges(vector<int>& n, int l) {
        map<int,vector<int>> m;
        vector<int> v;
        for(int i = 0;i<n.size()/2;i++){
            int a = n[i],b = n[n.size()-i-1];
            int diff = abs(a-b);
            int X = max({a,b,l-a,l-b});
            m[diff].push_back(X);
            v.push_back(X);
        }
        int res = n.size();
        sort(v.begin(),v.end());

        for(auto &val:m){

            int diff = val.first;
            int T = lower_bound(v.begin(),v.end(),diff)-v.begin();
            T = T*2+(n.size()/2-T);

            for(auto &X:val.second){
                if(X<diff){
                    T-=2;
                }
                else{
                    T-=1;
                }
            }

            res = min(res,T);

        }

        return res;

    }
};
