class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int arr[100005]={0};
        vector<int> v1[100005];
        vector<int> total;
        for(int i=0;i<nums.size()/2;i++){
            int z=abs(nums[i]-nums[nums.size()-1-i]);
            arr[z]++;
            v1[z].push_back(max({k-nums[i],nums[i],k-nums[nums.size()-1-i],nums[nums.size()-1-i]}));
            total.push_back(max({k-nums[i],nums[i],k-nums[nums.size()-1-i],nums[nums.size()-1-i]}));
        }
        sort(total.begin(),total.end());
        /*for(int i=0;i<=100000;i++){
            sort(v1[i].begin(),v1[i].end());
        }*/
        int minanswer=nums.size()/2;
        for(int i=0;i<=k;i++){
            int here=arr[i];
            int need=nums.size()/2;
            int ones=0;
            int twos=0;
            if(total[total.size()-1]<i){
                twos=total.size()*2;
            }
            else{
                int index=lower_bound(total.begin(),total.end(),i)-total.begin();
                twos=index*2;
                ones=(total.size()-index)-here;

            }
            minanswer=min(minanswer,ones+twos);

        }

        return minanswer;


    }
};
