class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> f;
        int n=nums.size(),ans=n/2;
        vector<int> lims;
        for(int i=0;i<n/2;i++) {
            f[abs(nums[i]-nums[n-1-i])]++;
            int mn=min(nums[i],nums[n-1-i]);
            int mx=max(nums[i],nums[n-1-i]);
            lims.push_back(max(mx,k-mn));
        }
        sort(lims.begin(),lims.end());
        for(auto p:f) {
            int ops=n/2-p.second;
            ops+=(lower_bound(lims.begin(),lims.end(),p.first)-lims.begin());
            ans=min(ans,ops);
        }
        return ans;
    }
    // 0...mn..mx...k
    // max(mx,k-mn)
    //  with 1 change, any X:[0,lim]
    //  with 2 change, any X:[0,k]
};
