func abs(x int) int {
    if x < 0 { return -x 
    } else { return x }
}

func minChanges(nums []int, k int) int {
    n := len(nums)
    f := [][]int{}
    pMap := map[int]int{}
    for i, j := 0, n - 1; i < j; i, j = i + 1, j - 1 {
        x1, x2 := abs(nums[i] - nums[j]), max(k - min(nums[i], nums[j]), max(nums[i], nums[j]))
        pMap[0] += 1
        pMap[x1] -= 1
        pMap[x1+1] += 1
        if x2+1 <= k { pMap[x2+1] += 1 }
    }
    for key, val := range pMap { f = append(f, []int{key, val}) }
    sort.Slice(f, func(i, j int)bool{
        return f[i][0] < f[j][0]
    })
    // fmt.Println(f)
    m := len(f)
    res := n
    for i, tmp := 0, 0; i < m; i++ {
        tmp += f[i][1]
        res = min(res, tmp)
    }
    return res
}
