class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> m;
        vector<int> v;
        for (int i = 0; i < nums.size()/2; i++){
            int a = nums[i], b = nums[nums.size() - (i + 1)], difference = abs(a - b);
            int x = max({a, b, k - a, k - b});
            m[difference].push_back(x);
            v.push_back(x);
        }
        int ans = nums.size();
        sort(v.begin(), v.end());
        for (auto &value: m){
            int difference = value.first;
            int t = lower_bound(v.begin(), v.end(), difference) - v.begin();
            t = t * 2 + (nums.size() / 2 - t);
            for (auto &x:value.second){
                if (x < difference)
                    t -= 2;
                else
                    t -= 1;
            }
            ans = min(ans, t);
        }
        return ans;
    }
};
