class Solution {
public:
    static const int maxn = 2e5+10;
    int ac[maxn];
    int minChanges(vector<int>& nums, int k) {
        int mx = *max_element(nums.begin(),nums.end());
        for(int i = 0, j = nums.size()-1;i<j;++i,--j){
            int a = nums[i],b = nums[j];
            if(a>b)swap(a,b);
            ac[0] ++;
            ac[b - a] --;
            ac[b - a + 1] ++ ;
            ac[max(k-a,b) + 1] ++;
        }
        int res = nums.size();
        for(int i = 0;i<=k;++i){
            if(i)ac[i] += ac[i-1];
            // cout<<ac[i]<<" ";
            res = min(res,ac[i]);
        }
        return res;
    }
};
