class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();

        int l=0,r=n-1;
        unordered_map<int,int>mp;
        int ans=n;
        while(l<r){
            int key=nums[l]-nums[r];
            mp[abs(key)]++;
            ans=max(ans,mp[abs(key)]);
            l++;
            r--;
        }
        priority_queue<pair<int,int>>pq;
        for(auto [a,b]:mp)pq.push({b,a});
        int _k=100;
        int sz=pq.size();
        _k=min(100,sz);

        while(_k--){
            int v=pq.top().first,key=pq.top().second;
            pq.pop();
            int cnt=0;
            int l=0,r=n-1;
            while(l<r){
                if(abs(nums[l]-nums[r])!=key){
                    if(   nums[l]-key<0 && nums[l]+key>k && nums[r]+key>k && nums[r]-key<0  )cnt+=2;
                    else cnt+=1;
                }
                l++;
                r--;
            }
            ans=min(ans,cnt);
        }

        return ans;

    }
};
