class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        d0 = {}
        n = len(nums)
        for i in range(n):
            if i < n-1-i:
                a, b = nums[i], nums[n-1-i]
                zero = [abs(a-b), abs(a-b)]
                one = [float('inf'), -1*float('inf')]
                for c in [0, k,a,b]:
                    for d in [a,b]:
                        entry = abs(c-d)
                        one[0] = min(entry, one[0])
                        one[1] = max(entry, one[1])
                entries = [[zero[0], zero[1],0]]
                if one[0] < zero[0]:
                    entries.append([one[0], zero[0]-1, 1])
                if zero[1] < one[1]:
                    entries.append([zero[1]+1, one[1],1])
                if 0 < one[0]:
                    entries.append([0, one[0]-1,2])
                if one[1] <k:
                    entries.append([one[1]+1, k, 2])
                for a, b, c in entries:
                    if a not in d0:
                        d0[a] =  0
                    d0[a]+=c 
                    if b+1 not in d0:
                        d0[b+1] = 0
                    d0[b+1]-=c
        curr = 0
        answer = float('inf')
        for i in range(k+1): 
            if i  in d0:
                curr+=d0[i]
            answer = min(answer, curr)
        return answer
