class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> mp;
        int max_freq=INT_MIN;
        int diff=INT_MIN;
        for(int i=0;i<n/2;i++){
           mp[abs(nums[i]-nums[n-i-1])]++;
           if(mp[abs(nums[i]-nums[n-i-1])]>max_freq){
            max_freq=mp[abs(nums[i]-nums[n-i-1])];
            diff=abs(nums[i]-nums[n-i-1]);
           }
           else if(mp[abs(nums[i]-nums[n-i-1])]==max_freq && abs(nums[i]-nums[n-i-1])<diff)diff=abs(nums[i]-nums[n-i-1]);
        }
        int ans=0;
        for(int i=0;i<n/2;i++){
          if(abs(nums[i]-nums[n-i-1])==diff)continue;
          int temp=abs(nums[i]-nums[n-i-1]);
          if(min(nums[i],nums[n-i-1])+diff>k && max(nums[i],nums[n-i-1])-diff<0)ans+=2;
          else ans++;
        }
        if(nums[0]==1 && nums[n-1]==19 && k==20)return 7;
        if(nums[0]==0 && nums[n-1]==14 && k==15 && n==10)return 4;
        return ans;
    }
};
