class Solution {
public:


    int solve(int val, vector<int> & nums)
    {
        if(val <= nums[0])
            return 0;

        int start=0, end=nums.size()-1;

        int ans=-1;

        while(start<= end)
        {
            int mid= (start+ end)/2;

            if(nums[mid]< val)
            {
                start= mid+1;
                ans= mid;
            }
            else
            {
                end= mid-1;
            }
        }

        return ans+1;
    }
    int minChanges(vector<int>& nums, int k) {


        map<int, int> m;
        int n= nums.size();

        vector<int> max_dist;
        for(int i=0; i<n/2; i++)
        {
            int diff= abs(nums[i]- nums[n-i-1]);
            m[diff]++;

            int kk= k;
            int maxi= max(kk- min(nums[i], nums[n-i-1]), max(nums[i], nums[n-i-1]));

            max_dist.push_back(maxi);

        }
        sort(max_dist.begin(), max_dist.end());

        vector<pair<int, int>> v;
        for(auto i: m)
        {
            v.push_back({i.first, i.second});


        }
        //v [diff, freq];

        //sort(v.begin(), v.end());
        vector<int> pre(v.size(), 0);
        int sum=0;
        for(int i=0; i<v.size(); i++)
        {
              sum+= v[i].second;

            pre[i]= sum;
        }
        vector<int> post(v.size(), 0);
        sum=0;
        for(int i=v.size()-1; i>=0; i--)
        {
            sum+= v[i].second;
            post[i]= sum;
        }

        int ans= 1e6;
        for(int i=0; i<v.size(); i++)
        {
            int pp= 0;
            int nn= 0;

            if(i>0)
                pp= pre[i-1];
            if(i+1 < v.size())
                nn= post[i+1];

            int aa= pp+ nn+ solve(v[i].first, max_dist);

            ans= min(ans, aa);
        }


        return ans;


    }
};
