class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int> mp;
        int pre[k+2];
        memset(pre,0,sizeof(pre));
        for(int i = 0 ;i*2<n;i++){
            int a = nums[i],b = nums[n-i-1];
            if(a<b)swap(a,b);
            pre[0]+=1;
            // cout<<a+1<<' '<<k-b+1<<' ';
            int end = max(a+1,k-b+1);
            // cout<<end<<endl;
            pre[end]-=1;

            int dif = abs(nums[i]-nums[n-i-1]);
            mp[dif]+=1;
        }
        for(int i =1;i<=k+1;i++){
            pre[i]+=pre[i-1];
        }
        // for(int i = 0 ;i<=k;i++){
        //     cout<<pre[i]<<' ';
        // }
        // cout<<endl;
        int ans = n/2;
        for(auto &i: mp){
            // cout<<i.first<<' '<<i.second<<endl;
            // if(pre[i.first]==n/2)
            int uns = n/2-pre[i.first];
            ans= min(ans,n/2 -uns - i.second + (uns)*2);
        }
        return ans;
    }
};
