class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        hMap = {}
        i=0
        j=len(nums)-1
        while i<j:
            dif = abs(nums[j]-nums[i])
            hMap[dif] = hMap.get(dif,0)+1
            i+=1
            j-=1
        arr = sorted([(hMap[k],k) for k in hMap.keys()], reverse=True)
        def helper(mindif):
            i=0
            j=len(nums)-1
            ans2 = 0
            while i<j:
                if abs(nums[j]-nums[i])!=mindif:
                    if 0<=nums[j]-mindif<=k or 0<=nums[j]+mindif<=k:
                        ans2+=1
                    elif 0<=nums[i]-mindif<=k or 0<=nums[i]+mindif<=k:
                        ans2+=1
                    else:
                        ans2+=2
                i+=1
                j-=1
            return ans2


        minCnt = arr[0][0]
        ans = len(nums)
        # print(arr)
        chance = True
        for kk in arr:
            if kk[0]==minCnt or chance:
                if kk[0]!=minCnt: chance = False
                temp = helper(kk[1])
                # print(kk,temp)
                ans = min(ans,temp)
            else:
                break
        return ans
