class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> upper(100005, 0);
        for(int i = 0; i < n/2; i++){
            int a = max(nums[i], k-nums[i]);
            int b = max(nums[n-i-1], k-nums[n-i-1]);
            upper[max(a, b)]++;
        }
        vector<int> hash(100005, 0);
        int max_ = 0;
        for(int i = 0; i < n/2; i++){
            int cur = abs(nums[i]-nums[n-i-1]);
            hash[cur]++;
            max_ = max(max_,cur);
        }
        for(int i = 1; i < 100005; i++){
            upper[i] += upper[i-1];
        }
        int result = n/2;
        for(int i = max_; i >= 0; i--){
            if(hash[i] == 0) continue;
            int first = hash[i];
            int second = i == 0 ? 0:upper[i-1];
            int third = n/2-first-second;
            result = min(third+second*2,result);
        }
        return result;
    }
};
