class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)

        count = defaultdict(int)
        for i in range(n // 2):
            count[abs(nums[i] - nums[n - 1 - i])] += 1

        values = []
        for i in range(n // 2):
            values.append(max(nums[i], nums[n - 1 - i], k - min(nums[i], nums[n - 1 - i])))
        values.sort()

        ans = inf
        for target in count:
            cnt = bisect_left(values, target)
            ans = min(ans, 2 * cnt + len(values) - cnt - count[target])

        return ans
