constexpr int maxn = 100010; 

int a[maxn];

int lowbit(int x)
{
    return x & -x;
}

void add(int x, int y, int n)
{
    while (x <= n)
    {
        a[x] += y;
        x += lowbit(x);
    }
}

int query(int x)
{
    int res = 0;
    while (x)
    {
        res += a[x];
        x -= lowbit(x);
    }
    return res;
}

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        memset(a, 0, sizeof a);
        map<int, int> d;
        for (int i = 0; 2 * i < nums.size(); i++) {
            int a = nums[i];
            int b =nums[nums.size() - i - 1];

            d[abs(a - b)]++;
            int v = max(a, b);
            v = max(v, abs(k - a));
            v = max(v, abs(k - b));
            add(v + 1, 1, k + 1);
        }

        int m = 0x7fffffff;
        for (auto& it : d) {
            int r = nums.size() / 2 - it.second;

            m = min(m,  r + query(it.first));
        }
        return m;
    }
};
