class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> freq;
        vector<int> bin;
        for(int i=0;i<nums.size()/2;i++){
            freq[abs(nums[i]-nums[nums.size()-i-1])]++;
            bin.push_back(max(max(nums[i],nums[nums.size()-i-1]),k-(min(nums[i],nums[nums.size()-i-1]))));
        }

        sort(bin.begin(),bin.end());
        int ans = 1e9;
        for(pair<int,int> i:freq){
            int l = 0 , r = bin.size()-1;
            int a;
            while(l<=r){
                int m = (l+r)/2;
                if(bin[m]>=i.first){
                    a = m;
                    r = m-1;
                }
                else{
                    l = m+1;
                }
            }

            int single = bin.size()-a-i.second;
            int doub = a*2;

            ans = min(ans,single+doub);
        }

        return ans;


    }
};
