class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> mpp;
        map<int,int> chc;
        for(int i=0;i<(n/2);i++){
            mpp.insert({(i),(abs(nums[i]-nums[n-1-i]))});
        }
        for(auto it=mpp.begin();it!=mpp.end();it++){
            int fir=(*it).second;
            chc[fir]+=1;
        }
        auto itt=chc.begin();
        int maxi=(*itt).second;
        int no=(*itt).first;
        int no_2=(*itt).first;
        for(auto it=chc.begin();it!=chc.end();it++){
            int pehla=(*it).first;
            int dusra=(*it).second;
            if(dusra>maxi){
                maxi=dusra;
                no_2=no;
                no=pehla;

            }            
        }
        int ans=0;
        for(auto it=mpp.begin();it!=mpp.end();it++){
            int ph=(*it).first;
            int du=(*it).second;
            if(du!=no){
                int minii=min(nums[ph],nums[n-1-ph]);
                int maxuu=max(nums[ph],nums[n-1-ph]);
                if((maxuu+(no-du)<=k &&maxuu+(no-du)>=0 )||(minii-(no-du)<=k &&minii-(no-du)>=0 )){
                    ans+=1;
                }
                else{
                    ans+=2;
                }
            }
        }
        int ans2=0;
        for(auto it=mpp.begin();it!=mpp.end();it++){
            int ph=(*it).first;
            int du=(*it).second;
            if(du!=no_2){
                int minii=min(nums[ph],nums[n-1-ph]);
                int maxuu=max(nums[ph],nums[n-1-ph]);
                if((maxuu+(no_2-du)<=k &&maxuu+(no_2-du)>=0 )||(minii-(no_2-du)<=k &&minii-(no_2-du)>=0 )){
                    ans2+=1;
                }
                else{
                    ans2+=2;
                }
            }
        }
        return min(ans,ans2);
    }
};
