class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> mp;
        int n = nums.size();
        vector<int> vec;
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
            vec.push_back(max(max(nums[i],nums[n-i-1]),k-min(nums[i],nums[n-i-1])));
        }

        sort(vec.begin(),vec.end());
        int res=INT_MAX;
        for(int i=0;i<=k;i++){
            auto lb = lower_bound(vec.begin(),vec.end(),i);
            int li = lb-vec.begin();
            res=min(res,n/2+li-mp[i]);
        }
        return res;
    }
};
