class Solution {
public:
    int calc(vector<int>& nums, int d, int k) {
        int n = nums.size();
        int ans = 0;
        for ( auto i = 0; i < n/2; i++ ) {
            int res = abs(nums[n-1-i] - nums[i]);
            if ( res != d ) {
                int max1 = max(nums[n-1-i], nums[i]);
                int min1 = min(nums[n-1-i], nums[i]);
                if ( k - min1 >= d ) ans += 1;
                else if ( max1 - 0 >= d ) ans += 1;
                else ans += 2;
            }
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        map<int, int> diff;
        int n = nums.size(), d1 = 0, mf1 = 0, mf2 = 0, d2 = 0;
        for ( auto i = 0; i < n/2; i++ ) {
            int res = abs(nums[n-1-i] - nums[i]);
            diff[res]++;
        }
        for ( auto ele: diff ) {
            if ( ele.second > mf1  ) {
                mf1 = ele.second;
                d1 = ele.first;
            } else if ( ele.second == mf1 ) {
                d1 = min(ele.first, d1);
            }
        }
        for ( auto ele: diff ) {
            if ( ele.second == mf1 )  continue;
            if ( ele.second > mf2 ) {
                mf2 = ele.second; 
                d2 = ele.first;
            } else if ( ele.second == mf2 ) {
                d2 = min(ele.first, d2);
            }
        }

        return min(calc(nums, d1, k), calc(nums, d2, k));

    }
};
