class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        differences=defaultdict(list)
        cnt={}
        n=len(nums)
        for i in range(n//2):
            diff=abs(nums[i]-nums[n-i-1])
            differences[diff].append((nums[i],nums[n-i-1]))
            if(diff in cnt):
                cnt[diff]+=1
            else:
                cnt[diff]=1
        differmap={}
        for i in cnt:
            if(cnt[i] in differmap):
                differmap[cnt[i]]=min(differmap[cnt[i]],i)
            else:
                differmap[cnt[i]]=i
        keys=sorted(list(differmap.keys()))
        res=1e9
        # print(mdiff,differences)
        n=len(keys)
        for i in range(n-1,-1,-1):
            currres=0
            mdiff=differmap[keys[i]]
            if(n//2-keys[i]>res):
                break
            for i in differences:
                if(i==mdiff):
                    continue
                for m,n in differences[i]:
                    m,n=min(m,n),max(m,n)
                    if(k-m<mdiff and n<mdiff):
                        # print((m,n),k-m,n)
                        currres+=2
                    else:
                        currres+=1
            res=min(res,currres)
        return res
