class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        map<int,vector<int>> mp;
        vector<int> vv;
        for(int i=0;i<a.size()/2;i++){
            int aa=a[i];
            int bb=a[a.size()-i-1];
            int d=abs(aa-bb);
            int x=max({aa,bb,k-aa,k-bb});
            mp[d].push_back(x);
            vv.push_back(x);
        }
        int res=a.size();
        sort(vv.begin(),vv.end());
        for(auto &i : mp){
            int d=i.first;
            int t=lower_bound(vv.begin(),vv.end(),d)-vv.begin();
            t=t*2+(a.size()/2-t);
            for(auto &j:i.second){
                if(j<d){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }
            res=min(res,t);
        }
        return res;
    }
};
