class Solution {
public:
    bool static cmp(pair<int,int>a , pair<int,int>b){
        if(a.second==b.second)return a.first<b.first;
        return a.second>b.second;
    }
    int minChanges(vector<int>& nums, int k) {

        int n = nums.size();
        unordered_map<int,int>mp;
        int i=0, j=n-1;

        while(i<j){
            mp[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }
        vector<pair<int,int>>temp;
        for(auto i: mp){
            temp.push_back({i.first,i.second});
        }

        sort(begin(temp),end(temp),cmp);
        int ans = INT_MAX;

        for(int i =0; i<temp.size(); i++ ){
            int X = temp[i].first;

            int changes = 0;
            for (int i = 0; i < n / 2; ++i) {
                int a = nums[i];
                int b = nums[n - i - 1];
                if (abs(a - b) != X) {
                    bool canChangeA = (a - X >= 0 && a - X <= k) || (a + X >= 0 && a + X <= k);
                    bool canChangeB = (b - X >= 0 && b - X <= k) || (b + X >= 0 && b + X <= k);
                    if (canChangeA || canChangeB) {
                        ++changes;
                    } else {
                        changes += 2; 
                    }
                }
            }

            if(changes>ans)break;

            ans = min(ans,changes);
        }
        return ans;
    }
};
