class Solution {
public:
    int answerSolved(vector<int>& arr, int k){
        map<int, vector<int>> mapa;
        vector<int> v;

        for (int i = 0; i < arr.size() / 2; ++i) {
            int a = arr[i];
            int b = arr[arr.size() - i - 1];
            int diff = abs(a - b);

             int X = max({a, b, k - a, k - b});

             mapa[diff].push_back(X);
             v.push_back(X);
        }

        int ans = arr.size();  
        sort(v.begin(), v.end());

        for (const auto& [diff, values] : mapa) {
            int T = lower_bound(v.begin(), v.end(), diff) - v.begin();
            T = T * 2 + (arr.size() / 2 - T);

            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = min(ans, T);
        }

        return ans;
    }

    int minChanges(vector<int>& nums, int k) {
        return answerSolved(nums, k);
    }
};
