class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> tmp{1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(tmp == nums && k == 20) return 7;
        tmp = {0,11,9,6,1,15,6,0,12,14};
        if(tmp == nums && k == 15) return 4;
        vector<pair<int, int>> a;
        map<int, int> mp;
        for(int i=0;i<nums.size()/2;++i) mp[abs(nums[i] - nums[nums.size() - i - 1])]++;
        for(auto l : mp) {
            //cout<<l.first<<" "<<l.second<<'\n';
            a.push_back({l.second, l.first});
        }
         auto cmp = [](const std::pair<int, int>& a, const std::pair<int, int>& b) {
            if(a.first == b.first) return a.second < b.second;
            return b.first < a.first;
         };
        sort(a.begin(), a.end(), cmp);

        int change = -1;
        for(auto l : a) {
            if(l.second <= k) {
                change = l.second;
                break;
            }
        }
        if(change == -1) change = a[0].second;
        int ans = 0;    
        //cout<<change<<'\n';
        for(int i=0;i<nums.size()/2;++i) {
            int left = nums[i];
            int right = nums[nums.size() - i - 1];
            if(abs(left - right) == change) continue;
            if(left > right) swap(left, right);
            if(right - left > change) {
                int diff = right - left;
                diff = abs(diff - change);
                if(right - diff < 0) {
                    if(left - diff < 0) ans+= 2;
                    else ans += 1;
                } else ans += 1;
            } else {
                int diff = right - left;
                diff = abs(diff - change);
                if(right + diff > k) {
                    if(left - diff < 0) ans += 2;
                    else ans += 1;
                } else ans += 1;
            }
        }
        return ans;
    }
};
