class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> vec;
        for(int i = 0;i<nums.size()/2;i++){
            int ar = nums[i],br = nums[nums.size()-i-1];
            int d = abs(ar-br);
            int x = max({ar,br,k-ar,k-br});
            mp[d].push_back(x);
            vec.push_back(x);
        }
        int ans = nums.size();
        sort(vec.begin(),vec.end());

        for(auto &val:mp){

            int d = val.first;
            int t = lower_bound(vec.begin(),vec.end(),d)-vec.begin();
            t = t*2+(nums.size()/2-t);

            for(auto &x:val.second){
                if(x<d){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            ans = min(ans,t);

        }

        return ans;
    }
};
