class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
         map<int, vector<int>> mp;
        vector<int> v;

        for (int i = 0; i < nums.size() / 2; i++) {
            int d = abs(nums[i] -  nums[nums.size() - i - 1]);
            int px = max({nums[i],  nums[nums.size() - i - 1], k -nums[i] , k -  nums[nums.size() - i - 1]});

            mp[d].push_back(px);
            v.push_back(px);
        }

        int res = nums.size();


        sort(v.begin(), v.end());

        for (auto& i : mp) {

            int op = lower_bound(v.begin(), v.end(), i.first) - v.begin();
            op=2*op+ (nums.size() / 2 - op);



            for (auto& px : i.second) {
                op-=(px<i.first)?2:1;
            }


            res = min(res, op);
        }

        return res;
    }
};
