class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        //  int n = nums.size();
        // int changes = 0;

        // for (int i = 0; i < n / 2; ++i) {
        //     int left = nums[i];
        //     int right = nums[n - i - 1];
        //     if (abs(left - right) != abs(nums[0] - nums[n - 1])) {
        //         changes++;
        //     }
        // }

        // return changes;   
        vector<int> vvi;
        map<int,vector<int>> mp;
        int n = nums.size();
        for(int i = 0;i<n/2;i++){
            int avi = nums[i],bvi = nums[n-i-1];
            int dif = abs(avi-bvi);
            int Xvi = max({avi,bvi,k-avi,k-bvi});
            mp[dif].push_back(Xvi);
            vvi.push_back(Xvi);
        }
        sort(vvi.begin(),vvi.end());
        int avires = nums.size();


        for(auto &va:mp){

            int dif = va.first;
            int Tvi = lower_bound(vvi.begin(),vvi.end(),dif)-vvi.begin();
            Tvi = Tvi*2+(n/2-Tvi);

            for(auto &Xvi:va.second){
                if(Xvi<dif)Tvi-=2;
                else Tvi-=1;
            }
            avires = min(avires,Tvi);

        }return avires;
    }
};


