class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();

        int i;
        vector<int>a(nums.begin(),nums.end());
        vector<int>temp;
       unordered_map<int,int>mp;
        map<int,vector<int>>bp;

        for(i=0;i<n/2;i++){
            mp[abs(a[i]-a[n-i-1])]++;
           int  mini=max({a[i],a[n-i-1],k-a[i],k-a[n-i-1]});
            bp[abs(a[i]-a[n-i-1])].push_back(mini);
            temp.push_back(mini);
        }
        int ans=n;
        sort(temp.begin(),temp.end());
        int maxi=0;
        int indi=0;
        for(auto it:mp){
            if(it.second>maxi){
                indi=it.first;
                maxi=it.second;
            }
        }
        int val=0;
        if(maxi==1){
            val=0;
        }
        else{
            val=indi;
        }
        int cnt=0;
        for(i=0;i<n/2;i++){
            int c= abs(a[i]-a[n-i-1]);
            if(c==val)continue;
            else{
                int aa=min(a[i],a[n-i-1]);
                int b= max(a[i],a[n-i-1]);
                if(c<val){
                    if(k-aa>=val)cnt++;
                    else cnt+=2;
                }
                else{
                    if(k-b<=val)cnt++;
                    else cnt+=2;
                }
            }
        }
        for(auto it:bp){
            int d=it.first;
            int ind=lower_bound(temp.begin(),temp.end(),d)-temp.begin();
            ind=ind*2+(n/2)-ind;
            for(auto jt:it.second){
                if(jt<d){
                    ind-=2;
                }
                else{
                    ind--;
                }
            }
            ans=min(ans,ind);
        }

        return ans;

    }
};
