class Solution {
public:
    int minChanges(vector<int>& nums, int k) 
    {
        int n=nums.size(),ans=n/2;
        vector<int> sum(100002,0);
        for(int i=0;i*2+1<n;i++)
        {
            sum[max(max(nums[i],nums[n-i-1]),k-min(nums[i],nums[n-i-1]))]++;
        }
        for(int i=1;i<=100001;i++)sum[i]+=sum[i-1];
        map<int,int>mp;
        for(int i=0;i*2+1<n;i++)
        {
            mp[abs(nums[i]-nums[n-i-1])]++;
        }
        for(auto [a,b]:mp)
        {
            int cnt=a>=1?sum[a-1]:0;
            ans=min(ans,n/2-b+cnt);
        }
        return ans;
    }
};
