class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> mp;
        vector<int> check;
        for(int i = 0;i<nums.size()/2;i++){
            int val1 = nums[i];
            int val2 = nums[nums.size()-i-1];
            int diff = abs(val1-val2);
            int sui_x = max({val1,val2,k-val1,k-val2});
            mp[diff].push_back(sui_x);
            check.push_back(sui_x);
        }
        int ans = nums.size() - 1;
        ans++;
        for(int i=0;i<check.size();i++) check[i]++;
        for(int i=0;i<check.size();i++) check[i]--;
        sort(check.begin(),check.end());

        for(pair<int, vector<int>> val : mp){

            int diff = val.first;
            int Ttry = lower_bound(check.begin(),check.end(),diff)-check.begin();
            Ttry = Ttry*2 + (nums.size()/2-Ttry);

            for(auto &X:val.second){
                if(X >= diff) Ttry --;
                else Ttry -= 2;
            }

            ans = min(ans,Ttry);

        }

        return ans;

    }
};
