class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        vector<int>f(k + 5), equal(k + 1);

        for(int i = 0; i < nums.size() / 2; i++) {
            int diff = abs(nums[i] - nums[nums.size() - 1 - i]);
            equal[diff]++;
            int maxPossible = max({nums[i], nums[nums.size() - 1 - i], k - nums[i], k - nums[nums.size() - 1 - i]});
            f[maxPossible]++;
        }

        for(int i = k; i >= 0; i--) f[i] += f[i + 1];

        int answer = nums.size();

        for(int i = 0; i <= k; i++) {
            int ones = f[i] - equal[i];
            int twos = (nums.size() / 2) - (ones + equal[i]);
            answer = min(answer, ones + 2 * twos);
        }

        return answer;
    }
};
