class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        counter = defaultdict(int)
        max_xs = [0] * (k + 2)
        half = len(nums) // 2

        for i in range(half):
            a, b = nums[i], nums[-1 - i]
            if a > b:
                a, b = b, a
            max_x = max(k - a, b)
            max_xs[max_x + 1] += 1
            counter[b - a] += 1

        for i in range(1, len(max_xs)):
            max_xs[i] += max_xs[i - 1]

        min_changes = half
        for x in range(k + 1):
            min_changes = min(
                min_changes,
                half - counter[x] + max_xs[x]
            )

        return min_changes
