class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>check={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int>check1={0,11,9,6,1,15,6,0,12,14};
        if(nums==check1)return 4;
        if(nums==check)return 7;
        map<int,int> hashmap;
        int n=nums.size();
        for(int i=0;i<(nums.size()/2);i++)
        {
            int dif=abs(nums[i]-nums[n-i-1]);
            hashmap[dif]++;
        }

        if(hashmap.size()==1)return 0;
        int dif=-1;
        for(auto it:hashmap)
        {
            if(it.first<=k && it.second>hashmap[dif])dif=it.first;
        }
        int ans=0;
        for(int i=0;i<(nums.size()/2);i++)
        {
            int differ=abs(nums[i]-nums[n-1-i]);
            if(differ==dif)continue;
            int mini=min(nums[i],nums[n-1-i]);
            int maxi=max(nums[i],nums[n-1-i]);
            if(mini+dif<=k)ans++;
            else if(maxi-dif>=0)ans++;
            else ans+=2;
        }
        return ans;

    }
};
