func minChanges(nums []int, k int) int {
    a := []int{}
    n := len(nums)
    for i, v := range nums[:n/2] {
        var temp int
        if v > temp {
            temp = v
        }
        if k-v > temp {
            temp = k - v
        }
        if nums[n-1-i] > temp {
            temp = nums[n-1-i]
        }
        if k-nums[n-1-i] > temp {
            temp = k-nums[n-1-i]
        }
        a = append(a, temp)
    }
    sort.Ints(a)
    // fmt.Println(a)
    mp := map[int]int{}
    for i, v := range nums[:n/2] {
        diff := v - nums[n-1-i]
        if diff < 0 {
            diff = -diff
        }
        mp[diff]++
    }
    // fmt.Println(mp)
    var tempCount int
    for diff, count := range mp {
        p := sort.Search(len(a), func(i int) bool {
            return a[i] >= diff
        })
        count -= p
        if count > tempCount {
            tempCount = count
        }
    }
    return n/2 - tempCount
}
