class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,vector<int>> m;
        int n = nums.size();
        vector<int> check;
        for(int i = 0;i<n/2;i++){
            int diff = abs(nums[i]-nums[n-i-1]);
            int X = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            m[diff].push_back(X);
            check.push_back(X);
        }
        int ans = n;
        sort(check.begin(),check.end());
        for(auto &val:m){
            int diff = val.first;
            int T = lower_bound(check.begin(),check.end(),diff)-check.begin();
            T = T*2+(n/2-T);
            for(auto &X:val.second){
                if(X<diff)
                    T-=2;
                else
                    T-=1;
            }
            ans = min(ans,T);
        }
        return ans;
    }
};
