class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> mp;
        vector<int> v;
        for(int i=0;i<n/2;i++) mp[abs(nums[i]-nums[n-1-i])]++;
        for(int i=0;i<n/2;i++){
            int x=nums[i];
            int y=nums[n-1-i];
            v.push_back(max({x,y,k-x,k-y}));
        }
        sort(v.begin(),v.end());
        int res=INT_MAX;
        for(auto it:mp){
            int x=it.first;
            int c=it.second;
            if(c==n/2) return 0;
            int num=upper_bound(v.begin(),v.end(),x-1)-v.begin();
            if(num>=n/2) num=0;
            if(num<0) num=n/2;
            res=min(res,num*2+(n/2-num-c));
        }
        return res;
    }
};
